/*\
title: $:/core/modules/filters/subfilter.js
type: application/javascript
module-type: filteroperator

Filter operator returning its operand evaluated as a filter

\*/
(function(){

/*jslint node: true, browser: true */
/*global $tw: false */
"use strict";

/*
Export our filter function
*/
exports.subfilter = function(source,operator,options) {
	var list = [], tiddlers = [],
	suffix = (operator.suffixes || [])[0] || [""];
	if (suffix.includes("tag")) {
		tiddlers = tiddlers.concat(options.wiki.getTiddlersWithTag(operator.operand));
	}
	if (suffix.includes("title")) {
		if (suffix.includes("tag")) {
			suffix.indexOf("title") > suffix.indexOf("tag") ?
				tiddlers.push(operator.operand) : tiddlers.unshift(operator.operand);
		} else {
			tiddlers[0] = operator.operand;
		}
	}
	if (suffix.includes("title") || suffix.includes("tag")) {
		tiddlers.forEach(function(title) {
			var tiddler = options.wiki.getTiddler(title);
			list = list.concat(options.wiki.filterTiddlers(tiddler.fields.text,options.widget,source));
		});
	} else {
		list = options.wiki.filterTiddlers(operator.operand,options.widget,source);
	}	
	if(operator.prefix === "!") {
		var results = [];
		source(function(tiddler,title) {
			if(list.indexOf(title) === -1) {
				results.push(title);
			}
		});
		return results;
	} else {
		return list;
	}
};

})();

\define custom-background-datauri()
<$set name="background" value={{$:/themes/tiddlywiki/vanilla/settings/backgroundimage}}>
<$list filter="[<background>is[image]]">
`background: url(`
<$list filter="[<background>!has[_canonical_uri]]">
`"`<$macrocall $name="datauri" title={{$:/themes/tiddlywiki/vanilla/settings/backgroundimage}}/>`"`
</$list>
<$list filter="[<background>has[_canonical_uri]]">
`"`<$view tiddler={{$:/themes/tiddlywiki/vanilla/settings/backgroundimage}} field="_canonical_uri"/>`"`
</$list>
`) center center;`
`background-attachment: `{{$:/themes/tiddlywiki/vanilla/settings/backgroundimageattachment}}`;
-webkit-background-size:` {{$:/themes/tiddlywiki/vanilla/settings/backgroundimagesize}}`;
-moz-background-size:` {{$:/themes/tiddlywiki/vanilla/settings/backgroundimagesize}}`;
-o-background-size:` {{$:/themes/tiddlywiki/vanilla/settings/backgroundimagesize}}`;
background-size:` {{$:/themes/tiddlywiki/vanilla/settings/backgroundimagesize}}`;`
</$list>
</$set>
\end

\define if-fluid-fixed(text,hiddenSidebarText)
<$reveal state="$:/themes/tiddlywiki/vanilla/options/sidebarlayout" type="match" text="fluid-fixed">
$text$
<$reveal state="$:/state/sidebar" type="nomatch" text="yes" default="yes">
$hiddenSidebarText$
</$reveal>
</$reveal>
\end

\define if-editor-height-fixed(then,else)
<$reveal state="$:/config/TextEditor/EditorHeight/Mode" type="match" text="fixed">
$then$
</$reveal>
<$reveal state="$:/config/TextEditor/EditorHeight/Mode" type="match" text="auto">
$else$
</$reveal>
\end

\rules only filteredtranscludeinline transcludeinline macrodef macrocallinline macrocallblock

/*
** Start with the normalize CSS reset, and then belay some of its effects
*/

{{$:/themes/tiddlywiki/vanilla/reset}}

*, input[type="search"] {
	box-sizing: border-box;
	-moz-box-sizing: border-box;
	-webkit-box-sizing: border-box;
}

html button {
	line-height: 1.2;
	color: <<colour button-foreground>>;
	background: <<colour button-background>>;
	border-color: <<colour button-border>>;
}

/*
** Basic element styles
*/

html {
	font-family: {{$:/themes/tiddlywiki/vanilla/settings/fontfamily}};
	text-rendering: optimizeLegibility; /* Enables kerning and ligatures etc. */
	-webkit-font-smoothing: antialiased;
	-moz-osx-font-smoothing: grayscale;
}

html:-webkit-full-screen {
	background-color: <<colour page-background>>;
}

body.tc-body {
	font-size: {{$:/themes/tiddlywiki/vanilla/metrics/fontsize}};
	line-height: {{$:/themes/tiddlywiki/vanilla/metrics/lineheight}};
	word-wrap: break-word;
	<<custom-background-datauri>>
	color: <<colour foreground>>;
	background-color: <<colour page-background>>;
	fill: <<colour foreground>>;
}

<<if-background-attachment """

body.tc-body {
        background-color: transparent;
}

""">>

h1, h2, h3, h4, h5, h6 {
	line-height: 1.2;
	font-weight: 300;
}

pre {
	display: block;
	padding: 14px;
	margin-top: 1em;
	margin-bottom: 1em;
	word-break: normal;
	word-wrap: break-word;
	white-space: {{$:/themes/tiddlywiki/vanilla/options/codewrapping}};
	background-color: <<colour pre-background>>;
	border: 1px solid <<colour pre-border>>;
	padding: 0 3px 2px;
	border-radius: 3px;
	font-family: {{$:/themes/tiddlywiki/vanilla/settings/codefontfamily}};
}

code {
	color: <<colour code-foreground>>;
	background-color: <<colour code-background>>;
	border: 1px solid <<colour code-border>>;
	white-space: {{$:/themes/tiddlywiki/vanilla/options/codewrapping}};
	padding: 0 3px 2px;
	border-radius: 3px;
	font-family: {{$:/themes/tiddlywiki/vanilla/settings/codefontfamily}};
}

blockquote {
	border-left: 5px solid <<colour blockquote-bar>>;
	margin-left: 25px;
	padding-left: 10px;
	quotes: "\201C""\201D""\2018""\2019";
}

blockquote > div {
	margin-top: 1em;
	margin-bottom: 1em;
}

blockquote.tc-big-quote {
	font-family: Georgia, serif;
	position: relative;
	background: <<colour pre-background>>;
	border-left: none;
	margin-left: 50px;
	margin-right: 50px;
	padding: 10px;
    border-radius: 8px;
}

blockquote.tc-big-quote cite:before {
	content: "\2014 \2009";
}

blockquote.tc-big-quote:before {
	font-family: Georgia, serif;
	color: <<colour blockquote-bar>>;
	content: open-quote;
	font-size: 8em;
	line-height: 0.1em;
	margin-right: 0.25em;
	vertical-align: -0.4em;
	position: absolute;
    left: -50px;
    top: 42px;
}

blockquote.tc-big-quote:after {
	font-family: Georgia, serif;
	color: <<colour blockquote-bar>>;
	content: close-quote;
	font-size: 8em;
	line-height: 0.1em;
	margin-right: 0.25em;
	vertical-align: -0.4em;
	position: absolute;
    right: -80px;
    bottom: -20px;
}

dl dt {
	font-weight: bold;
	margin-top: 6px;
}

button, textarea, input, select {
	outline-color: <<colour primary>>;
}

textarea,
input[type=text],
input[type=search],
input[type=""],
input:not([type]) {
	color: <<colour foreground>>;
	background: <<colour background>>;
}

input[type="checkbox"] {
  vertical-align: middle;
}

.tc-muted {
	color: <<colour muted-foreground>>;
}

svg.tc-image-button {
	padding: 0px 1px 1px 0px;
}

.tc-icon-wrapper > svg {
	width: 1em;
	height: 1em;
}

kbd {
	display: inline-block;
	padding: 3px 5px;
	font-size: 0.8em;
	line-height: 1.2;
	color: <<colour foreground>>;
	vertical-align: middle;
	background-color: <<colour background>>;
	border: solid 1px <<colour muted-foreground>>;
	border-bottom-color: <<colour muted-foreground>>;
	border-radius: 3px;
	box-shadow: inset 0 -1px 0 <<colour muted-foreground>>;
}

::selection {
	background-color: <<colour primary>>;
	color: <<colour page-background>>;
}

::-moz-selection {
	background-color: <<colour primary>>;
	color: <<colour page-background>>;
}

/*
Markdown likes putting code elements inside pre elements
*/
pre > code {
	padding: 0;
	border: none;
	background-color: inherit;
	color: inherit;
}

table {
	border: 1px solid <<colour table-border>>;
	width: auto;
	max-width: 100%;
	caption-side: bottom;
	margin-top: 1em;
	margin-bottom: 1em;
	/* next 2 elements needed, since normalize 8.0.1 */
	border-collapse: collapse;
	border-spacing: 0;
}

table th, table td {
	padding: 0 7px 0 7px;
	border-top: 1px solid <<colour table-border>>;
	border-left: 1px solid <<colour table-border>>;
}

table thead tr td, table th {
	background-color: <<colour table-header-background>>;
	font-weight: bold;
}

table tfoot tr td {
	background-color: <<colour table-footer-background>>;
}

.tc-csv-table {
	white-space: nowrap;
}

.tc-tiddler-frame img,
.tc-tiddler-frame svg,
.tc-tiddler-frame canvas,
.tc-tiddler-frame embed,
.tc-tiddler-frame iframe {
	max-width: 100%;
}

.tc-tiddler-body > embed,
.tc-tiddler-body > iframe {
	width: 100%;
	height: 600px;
}

/*
** Links
*/

button.tc-tiddlylink,
a.tc-tiddlylink {
	text-decoration: none;
	font-weight: 500;
	color: <<colour tiddler-link-foreground>>;
	-webkit-user-select: inherit; /* Otherwise the draggable attribute makes links impossible to select */
}

.tc-sidebar-lists a.tc-tiddlylink {
	color: <<colour sidebar-tiddler-link-foreground>>;
}

.tc-sidebar-lists a.tc-tiddlylink:hover {
	color: <<colour sidebar-tiddler-link-foreground-hover>>;
}

button.tc-tiddlylink:hover,
a.tc-tiddlylink:hover {
	text-decoration: underline;
}

a.tc-tiddlylink-resolves {
}

a.tc-tiddlylink-shadow {
	font-weight: bold;
}

a.tc-tiddlylink-shadow.tc-tiddlylink-resolves {
	font-weight: normal;
}

a.tc-tiddlylink-missing {
	font-style: italic;
}

a.tc-tiddlylink-external {
	text-decoration: underline;
	color: <<colour external-link-foreground>>;
	background-color: <<colour external-link-background>>;
}

a.tc-tiddlylink-external:visited {
	color: <<colour external-link-foreground-visited>>;
	background-color: <<colour external-link-background-visited>>;
}

a.tc-tiddlylink-external:hover {
	color: <<colour external-link-foreground-hover>>;
	background-color: <<colour external-link-background-hover>>;
}

/*
** Drag and drop styles
*/

.tc-tiddler-dragger {
	position: relative;
	z-index: -10000;
}

.tc-tiddler-dragger-inner {
	position: absolute;
	top: -1000px;
	left: -1000px;
	display: inline-block;
	padding: 8px 20px;
	font-size: 16.9px;
	font-weight: bold;
	line-height: 20px;
	color: <<colour dragger-foreground>>;
	text-shadow: 0 1px 0 rgba(0, 0, 0, 1);
	white-space: nowrap;
	vertical-align: baseline;
	background-color: <<colour dragger-background>>;
	border-radius: 20px;
}

.tc-tiddler-dragger-cover {
	position: absolute;
	background-color: <<colour page-background>>;
}

.tc-dropzone {
	position: relative;
}

.tc-dropzone.tc-dragover:before {
	z-index: 10000;
	display: block;
	position: fixed;
	top: 0;
	left: 0;
	right: 0;
	background: <<colour dropzone-background>>;
	text-align: center;
	content: "<<lingo DropMessage>>";
}

.tc-droppable > .tc-droppable-placeholder {
	display: none;
}

.tc-droppable.tc-dragover > .tc-droppable-placeholder {
	display: block;
	border: 2px dashed <<colour dropzone-background>>;
}

.tc-draggable {
	cursor: move;
}

.tc-sidebar-tab-open .tc-droppable-placeholder, .tc-tagged-draggable-list .tc-droppable-placeholder,
.tc-links-draggable-list .tc-droppable-placeholder {
	line-height: 2em;
	height: 2em;
}

.tc-sidebar-tab-open-item {
	position: relative;
}

.tc-sidebar-tab-open .tc-btn-invisible.tc-btn-mini svg {
	font-size: 0.7em;
	fill: <<colour muted-foreground>>;
}

/*
** Plugin reload warning
*/

.tc-plugin-reload-warning {
	z-index: 1000;
	display: block;
	position: fixed;
	top: 0;
	left: 0;
	right: 0;
	background: <<colour alert-background>>;
	text-align: center;
}

/*
** Buttons
*/

button svg, button img, label svg, label img {
	vertical-align: middle;
}

.tc-btn-invisible {
	padding: 0;
	margin: 0;
	background: none;
	border: none;
    	cursor: pointer;
	color: <<colour foreground>>;
}

.tc-btn-boxed {
	font-size: 0.6em;
	padding: 0.2em;
	margin: 1px;
	background: none;
	border: 1px solid <<colour tiddler-controls-foreground>>;
	border-radius: 0.25em;
}

html body.tc-body .tc-btn-boxed svg {
	font-size: 1.6666em;
}

.tc-btn-boxed:hover {
	background: <<colour muted-foreground>>;
	color: <<colour background>>;
}

html body.tc-body .tc-btn-boxed:hover svg {
	fill: <<colour background>>;
}

.tc-btn-rounded {
	font-size: 0.5em;
	line-height: 2;
	padding: 0em 0.3em 0.2em 0.4em;
	margin: 1px;
	border: 1px solid <<colour muted-foreground>>;
	background: <<colour muted-foreground>>;
	color: <<colour background>>;
	border-radius: 2em;
}

html body.tc-body .tc-btn-rounded svg {
	font-size: 1.6666em;
	fill: <<colour background>>;
}

.tc-btn-rounded:hover {
	border: 1px solid <<colour muted-foreground>>;
	background: <<colour background>>;
	color: <<colour muted-foreground>>;
}

html body.tc-body .tc-btn-rounded:hover svg {
	fill: <<colour muted-foreground>>;
}

.tc-btn-icon svg {
	height: 1em;
	width: 1em;
	fill: <<colour muted-foreground>>;
}

.tc-btn-text {
	padding: 0;
	margin: 0;
}

/* used for documentation "fake" buttons */
.tc-btn-standard {
	line-height: 1.8;
	color: #667;
	background-color: #e0e0e0;
	border: 1px solid #888;
	padding: 2px 1px 2px 1px;
	margin: 1px 4px 1px 4px;
}

.tc-btn-big-green {
	display: inline-block;
	padding: 8px;
	margin: 4px 8px 4px 8px;
	background: <<colour download-background>>;
	color: <<colour download-foreground>>;
	fill: <<colour download-foreground>>;
	border: none;
	border-radius: 2px;
	font-size: 1.2em;
	line-height: 1.4em;
	text-decoration: none;
}

.tc-btn-big-green svg,
.tc-btn-big-green img {
	height: 2em;
	width: 2em;
	vertical-align: middle;
	fill: <<colour download-foreground>>;
}

.tc-primary-btn {
 	background: <<colour primary>>;
}

.tc-sidebar-lists input {
	color: <<colour foreground>>;
}

.tc-sidebar-lists button {
	color: <<colour sidebar-button-foreground>>;
	fill: <<colour sidebar-button-foreground>>;
}

.tc-sidebar-lists button.tc-btn-mini {
	color: <<colour sidebar-muted-foreground>>;
}

.tc-sidebar-lists button.tc-btn-mini:hover {
	color: <<colour sidebar-muted-foreground-hover>>;
}

button svg.tc-image-button, button .tc-image-button img {
	height: 1em;
	width: 1em;
}

.tc-unfold-banner {
	position: absolute;
	padding: 0;
	margin: 0;
	background: none;
	border: none;
	width: 100%;
	width: calc(100% + 2px);
	margin-left: -43px;
	text-align: center;
	border-top: 2px solid <<colour tiddler-info-background>>;
	margin-top: 4px;
}

.tc-unfold-banner:hover {
	background: <<colour tiddler-info-background>>;
	border-top: 2px solid <<colour tiddler-info-border>>;
}

.tc-unfold-banner svg, .tc-fold-banner svg {
	height: 0.75em;
	fill: <<colour tiddler-controls-foreground>>;
}

.tc-unfold-banner:hover svg, .tc-fold-banner:hover svg {
	fill: <<colour tiddler-controls-foreground-hover>>;
}

.tc-fold-banner {
	position: absolute;
	padding: 0;
	margin: 0;
	background: none;
	border: none;
	width: 23px;
	text-align: center;
	margin-left: -35px;
	top: 6px;
	bottom: 6px;
}

.tc-fold-banner:hover {
	background: <<colour tiddler-info-background>>;
}

@media (max-width: {{$:/themes/tiddlywiki/vanilla/metrics/sidebarbreakpoint}}) {

	.tc-unfold-banner {
		position: static;
		width: calc(100% + 59px);
	}

	.tc-fold-banner {
		width: 16px;
		margin-left: -16px;
		font-size: 0.75em;
	}

}

/*
** Tags and missing tiddlers
*/

.tc-tag-list-item {
	position: relative;
	display: inline-block;
	margin-right: 7px;
}

.tc-tags-wrapper {
	margin: 4px 0 14px 0;
}

.tc-missing-tiddler-label {
	font-style: italic;
	font-weight: normal;
	display: inline-block;
	font-size: 11.844px;
	line-height: 14px;
	white-space: nowrap;
	vertical-align: baseline;
}

button.tc-tag-label, span.tc-tag-label {
	display: inline-block;
	padding: 0.16em 0.7em;
	font-size: 0.9em;
	font-weight: 400;
	line-height: 1.2em;
	color: <<colour tag-foreground>>;
	white-space: nowrap;
	vertical-align: baseline;
	background-color: <<colour tag-background>>;
	border-radius: 1em;
}

.tc-sidebar-scrollable .tc-tag-label {
	text-shadow: none;
}

.tc-untagged-separator {
	width: 10em;
	left: 0;
	margin-left: 0;
	border: 0;
	height: 1px;
	background: <<colour tab-divider>>;
}

button.tc-untagged-label {
	background-color: <<colour untagged-background>>;
}

.tc-tag-label svg, .tc-tag-label img {
	height: 1em;
	width: 1em;
	margin-right: 3px; 
	margin-bottom: 1px;
	vertical-align: text-bottom;
}

.tc-edit-tags button.tc-remove-tag-button svg {
	font-size: 0.7em;
	vertical-align: middle;
}

.tc-tag-manager-table .tc-tag-label {
	white-space: normal;
}

.tc-tag-manager-tag {
	width: 100%;
}

button.tc-btn-invisible.tc-remove-tag-button {
	outline: none;
}

/*
** Page layout
*/

.tc-topbar {
	position: fixed;
	z-index: 1200;
}

.tc-topbar-left {
	left: 29px;
	top: 5px;
}

.tc-topbar-right {
	top: 5px;
	right: 29px;
}

.tc-topbar button {
	padding: 8px;
}

.tc-topbar svg {
	fill: <<colour muted-foreground>>;
}

.tc-topbar button:hover svg {
	fill: <<colour foreground>>;
}

.tc-sidebar-header {
	color: <<colour sidebar-foreground>>;
	fill: <<colour sidebar-foreground>>;
}

.tc-sidebar-header .tc-title a.tc-tiddlylink-resolves {
	font-weight: 300;
}

.tc-sidebar-header .tc-sidebar-lists p {
	margin-top: 3px;
	margin-bottom: 3px;
}

.tc-sidebar-header .tc-missing-tiddler-label {
	color: <<colour sidebar-foreground>>;
}

.tc-advanced-search input {
	width: 60%;
}

.tc-search a svg {
	width: 1.2em;
	height: 1.2em;
	vertical-align: middle;
}

.tc-page-controls {
	margin-top: 14px;
	font-size: 1.5em;
}

.tc-page-controls .tc-drop-down {
  font-size: 1rem;
}

.tc-page-controls button {
	margin-right: 0.5em;
}

.tc-page-controls a.tc-tiddlylink:hover {
	text-decoration: none;
}

.tc-page-controls img {
	width: 1em;
}

.tc-page-controls svg {
	fill: <<colour sidebar-controls-foreground>>;
}

.tc-page-controls button:hover svg, .tc-page-controls a:hover svg {
	fill: <<colour sidebar-controls-foreground-hover>>;
}

.tc-menu-list-item {
	white-space: nowrap;
}

.tc-menu-list-count {
	font-weight: bold;
}

.tc-menu-list-subitem {
	padding-left: 7px;
}

.tc-story-river {
	position: relative;
}

@media (max-width: {{$:/themes/tiddlywiki/vanilla/metrics/sidebarbreakpoint}}) {

	.tc-sidebar-header {
		padding: 14px;
		min-height: 32px;
		margin-top: {{$:/themes/tiddlywiki/vanilla/metrics/storytop}};
	}

	.tc-story-river {
		position: relative;
		padding: 0;
	}
}

@media (min-width: {{$:/themes/tiddlywiki/vanilla/metrics/sidebarbreakpoint}}) {

	.tc-message-box {
		margin: 21px -21px 21px -21px;
	}

	.tc-sidebar-scrollable {
		position: fixed;
		top: {{$:/themes/tiddlywiki/vanilla/metrics/storytop}};
		left: {{$:/themes/tiddlywiki/vanilla/metrics/storyright}};
		bottom: 0;
		right: 0;
		overflow-y: auto;
		overflow-x: auto;
		-webkit-overflow-scrolling: touch;
		margin: 0 0 0 -42px;
		padding: 71px 0 28px 42px;
	}

	html[dir="rtl"] .tc-sidebar-scrollable {
		left: auto;
		right: {{$:/themes/tiddlywiki/vanilla/metrics/storyright}};
	}

	.tc-story-river {
		position: relative;
		left: {{$:/themes/tiddlywiki/vanilla/metrics/storyleft}};
		top: {{$:/themes/tiddlywiki/vanilla/metrics/storytop}};
		width: {{$:/themes/tiddlywiki/vanilla/metrics/storywidth}};
		padding: 42px 42px 42px 42px;
	}

<<if-no-sidebar "

	.tc-story-river {
		width: calc(100% - {{$:/themes/tiddlywiki/vanilla/metrics/storyleft}});
	}

">>

}

@media print {

	body.tc-body {
		background-color: transparent;
	}

	.tc-sidebar-header, .tc-topbar {
		display: none;
	}

	.tc-story-river {
		margin: 0;
		padding: 0;
	}

	.tc-story-river .tc-tiddler-frame {
		margin: 0;
		border: none;
		padding: 0;
	}
}

/*
** Tiddler styles
*/

.tc-tiddler-frame {
	position: relative;
	margin-bottom: 28px;
	background-color: <<colour tiddler-background>>;
	border: 1px solid <<colour tiddler-border>>;
}

{{$:/themes/tiddlywiki/vanilla/sticky}}

.tc-tiddler-info {
	padding: 14px 42px 14px 42px;
	background-color: <<colour tiddler-info-background>>;
